<?php
 class Platform_Getter { protected $_cache; public function __isset($key) { return method_exists($this, 'get_' . $key); } public function __get($key) { return call_user_func(array($this, 'get_' . $key)); } public function cached($key) { if( is_array($this->_cache) && isset($this->_cache[$key]) ) { return $this->_cache[$key]; } else { return null; } } public function set($key, $value = null) { if( is_array($key) ) { if( !is_array($this->_cache) ) { $this->_cache = array(); } $this->_cache = array_merge($this->_cache, $key); return true; } return $this->_cache[$key] = $value; } } 