<?php
 class Platform_Template { public function would_show($key) { if( Platform::feature($key) ) { if( Platform::feature($key . '_everywhere') || ipsController::$cmd instanceof public_forums_forums_boards ) { return true; } } return false; } public function __call($key, $args) { if( !$this->would_show($key) ) { return ''; } else { $class = Platform::toolbox()->registry->output->getTemplate('platform_global'); if( method_exists($this, 'process_' . $key) ) { $around = 'process_' . $key; return $this->$around(call_user_func_array( array($class, $key), $args ) ); } else { return call_user_func_array( array($class, $key), $args); } } } public function process_welcome($content) { return str_replace('{members_display_name}', Platform::toolbox()->memberData['members_display_name'], $content); } } 